package oth;

public class Position {
	private int x;
	private int y;
	
	
	
	public Position(int x, int y) {
		super();
		this.x = x;
		this.y = y;
	}
	public Position() {
		// TODO Auto-generated constructor stub
	}
	public int getX() {
		return x;
	}
	public int getY() {
		return y;
	}
	public void setX(int x) {
		this.x = x;
	}
	public void setY(int y) {
		this.y = y;
	}
	
	
	
	
}